import axios from 'axios';

function getIp() {
  const ip = fetch('https://ipapi.co/json/').then(d => d.json());
  return ip;
} 

export const getLocation = () => {
  const response = axios.get('http://www.travelpayouts.com/whereami?locale=ru&ip=' + getIp());
  return response;
}
