import { useState } from "react";
import advertisment1 from "../../assets/img/advertisement.jpg";
import advertisment2 from "../../assets/img/advertisement_2.jpg";
import advertisment3 from "../../assets/img/advertisement_3.png";

export function Slider() {
  const [active, setActive] = useState(0);
  let list = document.querySelector(".slider .list");
  let dots = document.querySelectorAll(".slider .dots li");

  //let active = 0;
  let lengthItems = 3;
  let sliderInterval = 3000;

  let refreshSlider = setInterval(next, sliderInterval);

  function next() {
    if (active + 1 > lengthItems) {
      active = 0;
    } else {
      active += 1;
    }
    reloadSlider();
  }
  function reloadSlider() {
    let checkLeft = items[active].offsetLeft;
    list.style.left = -checkLeft + "px";

    let lastActiveDot = document.querySelector(".slider .dots li.active");
    lastActiveDot.classList.remove("active");
    dots[active].classList.add("active");
    clearInterval(refreshSlider);
    refreshSlider = setInterval(next, sliderInterval);
  }

  setInterval(checkPageFocus, 200);

  function checkPageFocus() {
    if (!document.hasFocus()) {
      clearInterval(refreshSlider);
      refreshSlider = null;
    } else {
      if (!refreshSlider) {
        refreshSlider = setInterval(next, sliderInterval);
      }
    }
  }

  return (
    <section>
      <div class='container banner-container'>
        <div class='slider'>
          <div class='list'>
            <div class='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment1} alt='first slide' />
              </a>
            </div>
            <div class='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment2} alt='second slide' />
              </a>
            </div>
            <div class='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment3} alt='third slide' />
              </a>
            </div>
          </div>
          <ul class='dots'>
            <li
              onClick={() => {
                setActive(0);
                reloadSlider();
              }}
              class='active'
            ></li>
            <li
              onClick={() => {
                setActive(1);
                reloadSlider();
              }}
            ></li>
            <li
              onClick={() => {
                setActive(2);
                reloadSlider();
              }}
            ></li>
          </ul>
        </div>
      </div>
    </section>
  );
}
