import React, { useEffect, useState } from 'react';
import { Slider } from '../../components/Slider/Slider';

import "./Main.css";
import { getIp, getJoke } from '../../http/api/api';

const Main = () => {

  const [timezone, setTimezone] = useState();

  const [joke, setJoke] = useState();

  useEffect(() => {
    getIp().then((response) => setTimezone(response.timezone));
  }, [timezone]);

  useEffect(() => {
    getQuote().then((response) => {
      setJoke(response.setup + "\n" + response.punchline);
    });
  }, [joke, setJoke])

  return (
    <div>
      <div className='container timezone-container'>
        {timezone}
      </div>
      <div className='container timezone-container'>
        {joke}
      </div>
      <Slider />
    </div>
  );
};

export default Main;
