import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getNew } from "../../http/newsApi";

const NewsDetail = () => {
  const { id } = useParams();
  const [currentNew, setCurrentNew] = useState({});

  useEffect(() => {
    getNew(id).then((data) => setCurrentNew(data));
  }, [id]);

  return (
    <article class='container news-detail-container'>
      <img src={process.env.REACT_APP_API_URL + currentNew.img} alt='new-img' />
      <h3>{currentNew.title}</h3>
      <p>{currentNew.description}</p>
    </article>
  );
};

export default NewsDetail;
