import React from 'react';

export default class Registration extends React.Component {
  constructor() {
    super();
    let email;
    let emailIsValidate;
    let password;
    let passwordIsValidate;
    let repassword;
    this.state = {email, password, emailIsValidate, passwordIsValidate};

    this.onNameChange = this.onNameChange.bind(this);
    this.onAgeChange = this.onAgeChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
    emailValidate(email){
        return email.search(/^([A-z0-9_.-]{1,})@([A-z0-9_.-]{1,}).([A-z]{2,8})$/) === 1 ? true : false;
    }

    passwordValidate(password){
        return password.length() > 6;
    }

    onPasswordChange(e) {
        let val = e.target.value;
        let valid = this.passwordValidate(val);
        this.setState({password: val, passwordIsValidate: valid});
    }

    onRepasswordChange(e) {
      let val = e.target.value;

    }

    onEmailChange(e) {
        var val = e.target.value;
        console.log(val);
        var valid = this.validateName(val);
        this.setState({name: val, nameValid: valid});
    }

    handleSubmit(e) {
        e.preventDefault();
        if(this.state.nameValid ===true && this.state.ageValid===true){
            alert(`Имя: ${this.state.name} Возраст: ${this.state.age}`);
        }
    }

    render() {

        var emailColor = this.state.emailIsValidate ? "green" : "red";

        var passwordColor = this.state.passwordIsValidate ? "green" : "red";

        return (
            <form onSubmit={this.handleSubmit}>
                <p>
                    <label>Email:</label><br />
                    <input type="text" value={this.state.name} 
                        onChange={this.onNameChange} style={{borderColor: emailColor}} />
                </p>
                <p>
                    <label>Пароль:</label><br />
                    <input type="number" value={this.state.age} 
                        onChange={this.onAgeChange}  style={{borderColor: passwordColor}} />
                </p>
                <p>
                    <label>Повторите пароль:</label><br />
                    <input type="number" value={this.state.age} 
                        onChange={this.onAgeChange}  style={{borderColor: passwordColor}} />
                </p>
                <input type="submit" value="Отправить" />
            </form>
        );
    }
}
