import React, { useEffect, useState } from "react";
import { Slider } from "../../components/Slider/Slider";

import "./Main.css";
import { getIp, getJoke } from "../../http/api/api";

const Main = () => {
  const [timezone, setTimezone] = useState();

  const [joke, setJoke] = useState();

  const currentDate = (currentDateTime) => {
    const day = currentDateTime.getDay();
    const month = currentDateTime.getMonth();
    const year = currentDateTime.getFullYear();
    return `${day}.${month + 1}.${year}`;
  }

  useEffect(() => {
    try {
      getIp().then((response) => setTimezone(response.timezone)).catch((error) => console.log(error));
      getJoke().then((response) => {
        setJoke(response.setup + " " + response.punchline);
      });
    } catch (err) {
      console.log("failed");
    }
  }, []);

  return (
    <div>
      <div className='container timezone-container'>
        <p className='timezone-start'>{timezone}</p>
        <p className='timezone-end'>{currentDate(new Date())}</p>
        </div>
      <div className='container timezone-container'>{joke}</div>
      <Slider />
    </div>
  );
};

export default Main;
