import React from 'react';

export default class Registration extends React.Component {
  constructor() {
    super();
    let email;
    let emailIsValidate;

    let password;
    let passwordIsValidate;

    let repassword;
    let repasswordIsValidate;

    this.state = {email, password, repassword, emailIsValidate, passwordIsValidate, repasswordIsValidate};

    this.onEmailChange = this.onEmailChange.bind(this);
    this.onPasswordChange = this.onPasswordChange.bind(this);
    this.onRepasswordChange = this.onRepasswordChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
    emailValidate(email){
        return email.search(/^([A-z0-9_.-]{1,})@([A-z0-9_.-]{1,}).([A-z]{2,8})$/) === 1 ? true : false;
    }

    passwordValidate(password){
        return password.length() > 6;
    }

    onPasswordChange(e) {
        let val = e.target.value;
        let valid = this.passwordValidate(val);
        this.setState({password: val, passwordIsValidate: valid});
    }

    repasswordValidate(password){
      return password === this.state.password;
  }

    onRepasswordChange(e) {
      let val = e.target.value;
      let valid = this.repasswordValidate(val);
      this.setState({repassword: val, repasswordIsValidate: valid})
    }

    onEmailChange(e) {
        var val = e.target.value;
        console.log(val);
        var valid = this.validateName(val);
        this.setState({name: val, nameValid: valid});
    }

    handleSubmit(e) {
        e.preventDefault();
        if(this.state.nameValid ===true && this.state.ageValid===true){
            alert(`Имя: ${this.state.name} Возраст: ${this.state.age}`);
        }
    }

    render() {

        let emailColor = this.state.emailIsValidate ? "green" : "red";

        let passwordColor = this.state.passwordIsValidate ? "green" : "red";
        let repasswordColor = this.state.repasswordIsValidate ? "green" : "red";

        return (
            <form onSubmit={this.handleSubmit}>
                <p>
                    <label>Email:</label><br />
                    <input type="text" value={this.state.email} 
                        onChange={this.onEmailChange} style={{borderColor: emailColor}} />
                </p>
                <p>
                    <label>Пароль:</label><br />
                    <input type="password" value={this.state.password} 
                        onChange={this.on}  style={{borderColor: passwordColor}} />
                </p>
                <p>
                    <label>Повторите пароль:</label><br />
                    <input type="password" value={this.state.repassword} 
                        onChange={this.onAgeChange}  style={{borderColor: repasswordColor}} />
                </p>
                <input type="submit" value="Отправить" />
            </form>
        );
    }
}
