import React from "react";
import { useForm } from "react-hook-form";
import { NavLink } from "react-router-dom";

import "./Auth.css";
import { REGISTRATION_ROUTE } from "../../utils/constants";

const Auth = () => {
  const {
    handleSubmit,
    register,
    formState: { errors },
    reset,
  } = useForm({ mode: "onChange" });
  const onSubmit = (data) => {
    console.log(data);
    reset();
  };

  return (
    <div className='container'>
      <h2>Авторизация</h2>
      <form className='form' onSubmit={handleSubmit(onSubmit)}>
        <input
          type='email'
          className={errors.name && "error"}
          {...register("email", {
            required: "Поле email обязательно",
            pattern: {
              message: "Пожалуйста, введите валидный email",
              value: /^([A-z0-9_.-]{1,})@([A-z0-9_.-]{1,}).([A-z]{2,8})$/,
            },
          })}
          placeholder='Email'
        />
        {errors?.email && (
          <div style={{ color: "red" }}>{errors.email.message}</div>
        )}
        <input
          type='password'
          className={errors.name && "error"}
          {...register("password", {
            required: "Поле пароль обязательно",
            minLength: 6,
            pattern: {
              message: "Пароль должен содержать не меньше 6 символов",
            }
          })}
          placeholder='Password'
        />
        {errors?.password && (
          <div style={{ color: "red" }}>{errors.password.message}</div>
        )}
        <button>Войти</button>
      </form>
      <div className='sign-up'>
        <p>Уще нет аккаунта?</p>
        <NavLink className='sign-up_link' to={REGISTRATION_ROUTE}>
          Зарегистируйтесь
        </NavLink>
      </div>
    </div>
  );
};

export default Auth;
