import axios from 'axios';

function getIp() {
  const ip = fetch('https://ipapi.co/json/').then(d => d.json());
  return ip;
} 

export const getLocation = async () => {
  getIp().then()
  const response = await axios.get('http://ipwho.is/' + getIp());
  return response;
}
