import { useState, useEffect } from "react";
import advertisment1 from "../../assets/img/advertisement.jpg";
import advertisment2 from "../../assets/img/advertisement_2.jpg";
import advertisment3 from "../../assets/img/advertisement_3.png";

export function Slider() {
  const [active, setActive] = useState(0);
  const [left, setLeft] = useState(0);
  const [items, setItems] = useState();
  const [sliderInterval, setSliderInterval] = useState(0);
  const [refreshSlider, setRefreshSlider] = useState();

  useEffect(() => {
    setItems(document.querySelectorAll(".slider .list .item"));
    sliderInterval(3000); 
    setRefreshSlider(setInterval(next, sliderInterval));
  }, []);

  let dots = document.querySelectorAll(".slider .dots li");

  function next() {
    console.log(active);
    if (active === 3) {
      setActive(0);
    } else {
      setActive(active + 1);
    }
    reloadSlider();
  }
  function reloadSlider() {
    console.log(items);
    setLeft(items[active].offsetLeft);

    let lastActiveDot = document.querySelector(".slider .dots li.active");
    lastActiveDot.classList.remove("active");
    dots[active].classList.add("active");
    clearInterval(refreshSlider);
    setRefreshSlide(setInterval(next, sliderInterval));
  }

  setInterval(checkPageFocus, 200);

  function checkPageFocus() {
    if (!document.hasFocus()) {
      clearInterval(refreshSlider);
      refreshSlider = null;
    } else {
      if (!refreshSlider) {
        refreshSlider = setInterval(next, sliderInterval);
      }
    }
  }

  return (
    <section>
      <div className='container banner-container'>
        <div className='slider'>
          <div className='list' style={{offset: `${left}px`}}>
            <div className='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment1} alt='first slide' />
              </a>
            </div>
            <div className='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment2} alt='second slide' />
              </a>
            </div>
            <div className='item'>
              <a href='https://www.ozon.ru/category/shvabry-14618/'>
                <img src={advertisment3} alt='third slide' />
              </a>
            </div>
          </div>
          <ul className='dots'>
            <li
              onClick={() => {
                setActive(0);
                reloadSlider();
              }}
              className='active'
            ></li>
            <li
              onClick={() => {
                setActive(1);
                reloadSlider();
              }}
            ></li>
            <li
              onClick={() => {
                setActive(2);
                reloadSlider();
              }}
            ></li>
          </ul>
        </div>
      </div>
    </section>
  );
}
