import React, { useContext } from "react";

import "./Filters.css";
import { getServices } from "../../http/serviceAPI";
import { Context } from "../..";

function Filters() {
  const { service } = useContext(Context);

  const searchHandle = (e) => {
    getServices(service.page, service.limit, e.target.value).then((data) => {
      service.setServices(data.rows);
      service.setTotalService(data.total);

    });
  };

  return (
    <div className='filters'>
      <input
        className='search-input'
        type='text'
        placeholder='Поиск'
        onChange={(e) => searchHandle(e)}
      />
      <div className='radio-buttons'>
        <div className='radio-item'>
          <input type='radio' id='name1' value='name_asc' checked />
          <label for='name1'>По имени &uarr;</label>
        </div>

        <div className='radio-item'>
          <input type='radio' id='name2' value='name_desc' />
          <label for='name2'>По имени &darr;</label>
        </div>

        <div className='radio-item'>
          <input type='radio' id='price1' value='price_asc' />
          <label for='price1'>По цене &uarr;</label>
        </div>

        <div className='radio-item'>
          <input type='radio' id='price2' value='price_desc' />
          <label for='price2'>По цене &darr;</label>
        </div>
      </div>
    </div>
  );
}

export default Filters;
