import React, { useContext, useEffect } from "react";
import { observer } from "mobx-react-lite";
import Filters from "../../components/Filters";

import "./Services.css";
import ServiceList from "../../components/ServiceList";
import { Context } from "../..";
import { getServices } from '../../http/serviceAPI';
import Pages from '../../components/Pages';

const Services = observer(() => {
  const { service } = useContext(Context);

  useEffect(() => {
    getServices(service.page, service.limit, service.query).then((data) => {
      if(service.priceAsc) {
        service.setServices(data.rows.sort((a, b) => a.price - b.price));
      } else if(service.priceDesc) {
        service.setServices(data.rows.sort((a, b) => b.price - a.price));
      } else if (service.filters[2] ) {
        service.setServices(data.rows.sort((a, b) => a.price - b.price));
      } else if(service.filters[3]) {
        service.setServices(data.rows.sort((a, b) => b.price - a.price));
      }
      service.setTotalService(data.count)});
  }, [service, service.page, service.query, service.filters]);

  return (
    <div className='container services-container'>
      <Filters />
      <ServiceList />
      <Pages />
    </div>
  );
});

export default Services;
