import React, { useContext } from "react";
import { observer } from "mobx-react-lite";
import { useForm } from "react-hook-form";
import { NavLink } from "react-router-dom";

import { REGISTRATION_ROUTE } from "../../utils/constants";
import { login } from "../../http/userAPI";
import { Context } from "../..";

import "./Auth.css";

const Auth = observer(() => {
  const {
    handleSubmit,
    register,
    formState: { errors },
    reset,
  } = useForm({ mode: "onChange" });
  const onSubmit = (data) => {
    signIn(data);
    reset();
  };

  const { user } = useContext(Context);

  const signIn = async (data) => {
    try {
      const response = await login(data.email, data.password);
      user.setUser(response);
      user.setIsAuth(true);
    } catch (err) {
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success" sx={{ width: '100%' }}>
          This is a success message!
        </Alert>
      </Snackbar>
    }
  };

  return (
    <div className='container auth-container'>
      <h2>Авторизация</h2>
      <form className='form sign-in-form' onSubmit={handleSubmit(onSubmit)}>
        <input
          type='email'
          className={errors.name && "error"}
          {...register("email", {
            required: "Поле email обязательно",
            pattern: {
              message: "Пожалуйста, введите валидный email",
              value: /^([A-z0-9_.-]{1,})@([A-z0-9_.-]{1,}).([A-z]{2,8})$/,
            },
          })}
          placeholder='Email'
        />
        {errors?.email && (
          <div style={{ color: "red" }}>{errors.email.message}</div>
        )}
        <input
          type='password'
          className={errors.name && "error"}
          {...register("password", {
            required: "Поле пароль обязательно",
            pattern: {
              value: /^([A-z0-9!@#$%^&*()]{6,})$/,
              message: "Пароль должен содержать минимум 6 символов",
            },
          })}
          placeholder='Password'
        />
        {errors?.password && (
          <div style={{ color: "red", maxWidth: "300px" }}>
            {errors.password.message}
          </div>
        )}
        <button>Войти</button>
      </form>
      <div className='sign-in'>
        <p>Ещё нет аккаунта?</p>
        <NavLink className='sign-in_link' to={REGISTRATION_ROUTE}>
          Зарегистируйтесь
        </NavLink>
      </div>
    </div>
  );
});

export default Auth;
